import React, { Component } from 'react'
import { Table,  Modal, Container, Button, Form, Row, Col } from 'react-bootstrap';
import "bootstrap/dist/css/bootstrap.min.css"
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faEdit, faTrashAlt } from '@fortawesome/free-solid-svg-icons'

class EmpleadoPlanilla extends Component {
    constructor(...props) {
        super(...props);
        this.state = {
            empleadoP: [], show: false,
            form: {
                idusuario :"",
                ocupacion:"",
                fecha_ingreso:"",
                fecha_salida:""

            }
        }
    }

    cargarDatos() {
        fetch('http://localhost:4201/api/empleado-planilla',
            {
                method: "GET",
                headers: {
                    'Content-Type': 'application/json'
                }
            })
            .then((response) => {
                return response.json()
            })
            .then((res) => {
                this.setState({
                    empleadoP: res.empleadoP
                })
            })
            .catch(function (error) {
                console.log('Hubo un problema con la petición:' + error.message);
            });
    }
    componentDidMount() {
        this.cargarDatos();
    }
    handleModal() {
        this.setState({ show: !this.state.show })
    }
    //segundoPlano
    handleChange = async e => {
        e.persist();
        await this.setState({
            form:{
            ...this.state.form,
            [e.target.idusuario]:e.target.value
            }
        })
        console.log(this.state.form)
    }

    render() {
        const { empleadoP } = this.state;
        console.log('Empleado', empleadoP)
        return (
            <>
                <Container>
                    <h2>Lista de Empleados</h2>
                    <br />
                    <button className="btn btn-success" onClick={() => { this.handleModal() }} >Agregar Empleado </button>
                    <br />
                    <br />
                    <Table striped bordered hover variant="dark">
                        <thead>
                            <tr>
                                <th>idusuario</th>
                                <th>ocupacion</th>
                                <th>Fecha de Ingres</th>
                                <th>Fecha de salida</th>
                                <th>Opciones</th>
                            </tr>
                        </thead>
                        <tbody>
                            {
                                (empleadoP)
                                    ?
                                    empleadoP.map(item => (

                                        <tr key={item._id}>
                                            <td>{item.idusuario}</td>
                                            <td>{item.ocupacion}</td>
                                            <td>{item.fecha_ingreso}</td>
                                            <td>{item.fecha_salida}</td>
                                            <td>
                                                <button className="btn btn-primary"> <FontAwesomeIcon icon={faEdit}></FontAwesomeIcon>   </button>
                                                {"   "}


                                                <button className="btn btn-danger"> <FontAwesomeIcon icon={faTrashAlt}></FontAwesomeIcon>   </button>

                                            </td>
                                        </tr>

                                    ))
                                    :
                                    <tr></tr>}
                        </tbody>
                    </Table>
                    <Modal show={this.state.show} onHide={() => this.handleModal()} >
                        <Modal.Header closeButton> Agregar Empleado
                            {/* <Modal.Title>Modal heading</Modal.Title> */}
                        </Modal.Header>
                        <Modal.Body>
                            <Form>
                                <Row>
                                    <Col>
                                        <Form.Group controlId="formBasicEmail">
                                            <Form.Label>Id Usuario</Form.Label>
                                            <input type="text" placeholder="Id Usuario" name="idusuario" onChange={this.handleChange}/>
                                        </Form.Group>
                                    </Col>
                                    <Col>
                                        <Form.Group controlId="formBasictext">
                                            <Form.Label>Ocupación</Form.Label>
                                            <input type="text" placeholder="Ocupacion"name="ocupacion" onChange={this.handleChange}/>
                                        </Form.Group>
                                    </Col>
                                </Row>
                                <Row>
                                    <Col>
                                        <Form.Group controlId="formBasictext">
                                            <Form.Label>Fecha Inicio </Form.Label>
                                            <input type="date"name="fecha_ingreso" onChange={this.handleChange}/>
                                        </Form.Group>
                                    </Col>
                                    <Col>
                                        <Form.Group controlId="formBasictext">
                                            <Form.Label>Fecha Fin </Form.Label>
                                            <input type="date" name="fecha_salida"onChange={this.handleChange}/>
                                        </Form.Group>
                                    </Col>
                                </Row>
                            </Form>
                        </Modal.Body>
                        <Modal.Footer>
                            <Button variant="secondary" onClick={() => { this.handleModal() }}>
                                Cancelar
                          </Button>
                            <Button variant="primary" >
                                Save
                           </Button>
                        </Modal.Footer>
                    </Modal>
                </Container>
            </>
        )
    }
}
export default EmpleadoPlanilla
