import React, { Component } from 'react'
import { Table, Card, Modal, ModalBody, ModalFooter, ModalHeader, Container,Button } from 'react-bootstrap';
import "bootstrap/dist/css/bootstrap.min.css"
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faEdit, faTrashAlt } from '@fortawesome/free-solid-svg-icons'
//import { Button } from 'bootstrap';


class EmpleadoPlanilla extends Component {
    constructor(...props) {
        super(...props);
        this.state = {
            empleadoP: [], show: false,
        }
    }
    modalInsertar = () => {
        this.setState({ modalInsertar: !this.state.modalInsertar })
    }
    cargarDatos() {
        fetch('http://localhost:4201/api/empleado-planilla',
            {
                method: "GET",
                headers: {
                    'Content-Type': 'application/json'
                }
            })
            .then((response) => {
                return response.json()
            })
            .then((res) => {
                this.setState({
                    empleadoP: res.empleadoP
                })
            })
            .catch(function (error) {
                console.log('Hubo un problema con la petición:' + error.message);
            });
    }
    componentDidMount() {
        this.cargarDatos();
    }
    handleModal()
    {
        this.setState({show:true})

    }
        
    render() {

        const { empleadoP } = this.state;
        console.log('Empleado', empleadoP)
        return (
            <>
                <Container>
                    <h2>Lista de Empleados</h2>
                    <br />
                    <Button className="btn btn-success" onclick={()=>{this.handleModal()}} >Agregar Empleado </Button>
                    <br />
                    <br />
                    <Table striped bordered hover variant="dark">
                        <thead>
                            <tr>
                                <th>idusuario</th>
                                <th>ocupacion</th>
                                <th>Fecha de Ingres</th>
                                <th>Fecha de salida</th>
                                <th>Opciones</th>
                            </tr>
                        </thead>
                        <tbody>
                            {
                                (empleadoP)
                                    ?
                                    empleadoP.map(item => (

                                        <tr key={item._id}>
                                            <td>{item.idusuario}</td>
                                            <td>{item.ocupacion}</td>
                                            <td>{item.fecha_ingreso}</td>
                                            <td>{item.fecha_salida}</td>
                                            <td>
                                                <button className="btn btn-primary"> <FontAwesomeIcon icon={faEdit}></FontAwesomeIcon>   </button>
                                                {"   "}


                                                <button className="btn btn-danger"> <FontAwesomeIcon icon={faTrashAlt}></FontAwesomeIcon>   </button>

                                            </td>
                                        </tr>

                                    ))
                                    :
                                    <tr></tr>}
                        </tbody>
                    </Table>
                    <Modal show={this.state.show} onHide={()=>this.handleModal()} >
                        <Modal.Header closeButton>
                            {/* <Modal.Title>Modal heading</Modal.Title> */}
                        </Modal.Header>
                        <Modal.Body>Woohoo, you're reading this text in a modal!</Modal.Body>
                        <Modal.Footer>
                            <Button variant="secondary" onClick={()=>{this.handleModal()}}>
                                Close
                          </Button>
                            <Button variant="primary" >
                                Save Changes
                           </Button>
                        </Modal.Footer>
                    </Modal>
                    {/* 
                    <Modal isOpen={this.state.modalInsertar}>
                        <Modal.Header style={{ display: 'block' }}>
                            <span style={{ float: 'right' }}>x</span>
             
                        </Modal.Header>
                        <Modal.Body className="show-grid">
                            <div className="form-group">
                                <label htmlFor="id"> ID</label>
                                <input className="form-control" type="text" name="idUsuario" />
                                <br />
                                <label htmlFor="id"> Fecha Inicio</label>
                                <input className="form-control" type="date" name="fecha_inicio" />
                                <br />
                                <label htmlFor="id"> Fecha Fin</label>
                                <input className="form-control" type="date" name="fecha_salida" />
                                <br />


                            </div>
                        </Modal.Body>
                        <Modal.Footer>
                            <button className="btn btn-success" >Insertar</button>
                            <button className="btn btn-danger" onClick={() => this.modalInsertar()} >Cancelar</button>
                        </Modal.Footer>
                    </Modal> */}


                </Container>
            </>
        )
    }
}
export default EmpleadoPlanilla
